  CREATE TABLE "NLFWA"."METHOD" 
   (	"STATE" CHAR(2 BYTE), 
	"METHOD" VARCHAR2(50 BYTE), 
	"LNAME" VARCHAR2(20 BYTE), 
	"FNAME" VARCHAR2(15 BYTE), 
	"NAME" VARCHAR2(50 BYTE), 
	"TITLE" VARCHAR2(50 BYTE), 
	"PHONE_NUMB" VARCHAR2(100 BYTE), 
	"AGENCY" VARCHAR2(200 BYTE), 
	"ADDRESS" VARCHAR2(100 BYTE), 
	"CITY" VARCHAR2(35 BYTE), 
	"ZIP" VARCHAR2(15 BYTE), 
	"FAX" VARCHAR2(20 BYTE), 
	"EMAIL" VARCHAR2(50 BYTE), 
	"STATEURL" VARCHAR2(250 BYTE), 
	"STATEURLTEXT" VARCHAR2(100 BYTE), 
	"CURRINFO" CHAR(1 BYTE), 
	"AFFILIATION" VARCHAR2(20 BYTE), 
	"IS_DEFAULT" CHAR(1 BYTE) DEFAULT 'N'
   ) ;

