   COMMENT ON COLUMN "NLFWA"."METHOD"."ADDRESS" IS 'Street address';
   COMMENT ON COLUMN "NLFWA"."METHOD"."CITY" IS 'City';
   COMMENT ON COLUMN "NLFWA"."METHOD"."ZIP" IS 'Zip';
   COMMENT ON COLUMN "NLFWA"."METHOD"."FAX" IS 'Fax number';
   COMMENT ON COLUMN "NLFWA"."METHOD"."EMAIL" IS 'E mail address';
   COMMENT ON COLUMN "NLFWA"."METHOD"."STATEURL" IS 'URL of organizations fish consumption advisory web site';
   COMMENT ON COLUMN "NLFWA"."METHOD"."STATEURLTEXT" IS 'Text description of orgs fish consumption advisory web site';
   COMMENT ON COLUMN "NLFWA"."METHOD"."CURRINFO" IS 'Weather Contact information is known to be current';
   COMMENT ON COLUMN "NLFWA"."METHOD"."AFFILIATION" IS 'Type of entity this contact is affiliated with';
   COMMENT ON COLUMN "NLFWA"."METHOD"."IS_DEFAULT" IS 'Flag indicating whether this contact is the "primary" contact for the state.  NOTE: Each state must have at most ONE record marked as "Y", otherwise any queries expecting only ONE primary record per state will fail.';
